CREATE TABLE `article` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '主键，自增长的文章编号',
  `article_id` varchar(50) NOT NULL COMMENT '文章的唯一标识符',
  `user_id` varchar(50) DEFAULT NULL COMMENT '发布文章的用户 ID',
  `user_name` varchar(50) DEFAULT NULL COMMENT '发布文章的用户昵称',
  `user_image` varchar(200) DEFAULT NULL COMMENT '发布文章的用户头像链接',
  `article_type` varchar(10) DEFAULT NULL COMMENT '0 学习，1是生活，2是其他',
  `article_content` text COMMENT '文章的内容',
  `article_image` text COMMENT '文章中包含的图片链接',
  `article_video` text COMMENT '文章中包含的视频链接',
  `views_count` int DEFAULT '0' COMMENT '文章的转发数',
  `comment_count` int DEFAULT '0' COMMENT '文章的评论数',
  `like_count` int DEFAULT '0' COMMENT '文章的点赞数',
  `create_time` datetime DEFAULT NULL COMMENT '文章的创建时间',
  `source` varchar(100) DEFAULT NULL COMMENT '文章的来源，例如：Android 客户端、iPhone 客户端、网页版等',
  `location` varchar(100) DEFAULT NULL COMMENT '发布文章的地理位置',
  `topic` varchar(100) DEFAULT NULL COMMENT '文章的话题标签',
  `category` int COMMENT '文章是类型',
  `is_long` tinyint(1) DEFAULT '0' COMMENT '文章是否为长微博，1 表示是，0 表示否',
  `attitude` varchar(10) DEFAULT NULL COMMENT '用户对文章的态度，例如：赞、踩、中立等',
  `status` int DEFAULT '0',
  PRIMARY KEY (`id`),
  UNIQUE KEY `article_id` (`article_id`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='用户文章表';
